import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'


const PokemonScreen = () => {

    const [loading, setLoading] = useState(true)
    const [pokemonData, setPokemonData] = useState()

    let params = useParams()
    const pokemonId = params.id 

    const fetchPokemonDetails = async () => {
        console.log('fetching Data');
        const url = `https://pokeapi.co/api/v2/pokemon/${pokemonId}/`
        const response = await fetch(url);
        const data = await response.json();
        console.log(data)
        setPokemonData(data)
        setLoading(false)
    }

    useEffect(() => {
        fetchPokemonDetails()
    }, [])

    return (
        <div>
            {loading ? <div>...Loading</div> : (
                <div className="pokemon-details-wrapper">
                    <img src={`${pokemonData.sprites.front_default}`}></img>
                    <img src={`${pokemonData.sprites.other.dream_world}`}></img>
                    <p className="pokemon-details-name">Name: {pokemonData.name}</p>
                    <p className="pokemon-details-name">Abilities: {pokemonData.name}</p>
                    <p className="pokemon-details-name">Weight: {pokemonData.weight}</p>
                    <div className="pokemon-details-abilities">
                            {pokemonData.abilities.map((data, i) => {
                            return <p>Ability {i + 1}: {data.ability.name}</p>
                            })
                        }
                    </div>
                    <div className="pokemon-details-types">
                            {pokemonData.types.map((data, i) => {
                            return <p>Type {i + 1}: {data.type.name}</p>
                            })
                        }
                    </div>
                    <div className="pokemon-details-types">
                            {pokemonData.moves.map((data, i) => {
                            return <p>Moves {i + 1}: {data.move.name}</p>
                            })
                        }
                    </div>

                </div>
            )}
        </div>
    )
}

export default PokemonScreen
